import copy

# Used to counter Time Limit Exceeded from first try 
# Hard to incorporate, too many use case afterwards and problem still remains
def findPInS(s,p):
    
    
    cpt = 0
    if p not in s: return False
    
    tmp = copy.deepcopy(s)
    L = []
    
    while p in tmp:
        k = tmp.index(p)    # searching p 
        L.append(k+cpt)     # correct index appending
        cpt += k+1          # index correction
        s = list(tmp)       # transforming to list to use pop
        s.pop(k)                  
        tmp = "".join(s)
        
    return L

