# Time Limit Exceed
def findAnagrams(s, p):
    R = []
    
    for i in range(len(s)):
        if s[i] not in p: continue
            
        tmp = list(copy.deepcopy(p))
        tmp.remove(s[i])
        anagram = True
        k = 1
        
        while len(tmp)>0:
            
            if i+k>=len(s) or s[i+k] not in tmp:
                anagram = False
                tmp = []
                continue
        
            tmp.remove(s[i+k])
            k += 1
            
        if anagram and tmp == []: R.append(i)
            
    return R
                



