# solution from discussion - understanding ok
def findAnagrams2(s, p):
    res = []
    if len(p)>len(s): return res
    D = dict()
    for c in list(p):
        if c in D: D[c] += 1
        else: D[c] = 1
    counter = len(D)
    
    begin = 0
    end = 0
    
    S = list(s)
    
    while end < len(s):
        c = S[end]
        if c in D:
            D[c] -= 1
            if D[c] == 0: counter -= 1
        end += 1
        
        while counter == 0:
            tempc = S[begin]
            if tempc in D:
                D[tempc] += 1
                if D[tempc] > 0 : counter += 1
            
            if end-begin == len(p):
                res.append(begin)
            begin += 1
            
    return res
            
            